import React, {useState} from "react";
import "./Experience.css"

function Experience() {

    const jobs = [
        {value: "JPMC", label: "Software Engineer"},
        {value: "TI", label: "Product Marketing Engineer"},
        {value: "IEEE", label: "Director of Techonology"},
    ]

    const [selectedJob, setSelectedJob] = useState("JPMC");

    const handleChange = (event) => {
        // i want to increase the font size/ bold it to make it know which job is selected
        // make the previous item that was clicked on go back to its original font size
        // display the correct bullet points for each job
        setSelectedJob(event.target.value);
        console.log("clicked")
    }


    return (
        <section id="Experience">
            <h1 className="font-bold text-lg mb-8 sm:text-2xl">Work Experience</h1>
            <div className="flex flex-col">
                <form className="flex gap-8 text-sm justify-between border-b border-black job-group">
                    {jobs.map( 
                        (job) => (
                                <label key={job.value} className={selectedJob === job.value ? "font-bold": ""}>
                                    <input
                                    type="radio"
                                    name="jobs"
                                    value={job.value}
                                    checked={job.value === selectedJob}
                                    onChange={handleChange}
                                    className="hidden"
                                    />
                                    {job.label}
                                </label>
                            )

                        )
                    }
                </form>
                <ul id="JPMC" className="px-4 list-disc">
                    <li>filler 1</li>
                    <li>filler 1</li>
                    <li>filler 1</li>
                </ul>
                <ul id="TI" className="px-4 list-disc hidden">
                    <li>filler 2</li>
                    <li>filler 2</li>
                    <li>filler 2</li>
                </ul>
                <ul id="IEEE" className="px-4 list-disc hidden">
                    <li>filler 3</li>
                    <li>filler 3</li>
                    <li>filler 3</li>
                </ul>
            </div>
        </section>
    )
}

export default Experience
